SELECT scrubber_sentence.id, scrubber_sentence.originalText, scrubber_sentence.alteredText FROM scrubber_sentence, scrubber_task WHERE scrubber_task.status = -1 AND (scrubber_sentence.id = scrubber_task.msg_a1_id OR scrubber_sentence.id = scrubber_task.msg_a2_id OR scrubber_sentence.id = scrubber_task.msg_b1_id OR scrubber_sentence.id = scrubber_task.msg_b2_id OR scrubber_sentence.id = scrubber_task.msg_c1_id);
